"use strict";
var b = require("bobril");
var g = require("bobril-g11n");
var f = require("./src/flux");
var s = require("./src/states");
var c = require("./src/states.cursors");
var mp = require("./src/mainPage");
g.initGlobalization({
    defaultLocale: 'cs-CZ',
    pathToTranslation: function (locale) { return './translations/' + locale + '.js'; }
});
f.bootstrap(s.createDefaultApplicationState(), {});
b.routes(b.route({
    url: "/",
    handler: mp.createMainPage(c.rootCursor)
}, mp.createRoutes()));
g.setLocale('cs-CZ');
