"use strict";
var m = require("bobril-m");
var fg = require("bobril-flexbox-grid");
var f = require("../flux");
exports.createWhatNextPage = f.createRouteComponent({
    render: function (ctx, me) {
        me.children = ctx.state.sources.map(function (bs) { return m.Paper({
            children: [
                fg.Row({
                    center: 1 /* xs */,
                    children: fg.Col({
                        md: 10,
                        children: { tag: "h3", children: bs.name }
                    })
                }),
                fg.Row({
                    center: 1 /* xs */,
                    children: fg.Col({
                        md: 10,
                        children: bs.description
                    })
                })
            ]
        }); });
    }
});
